package pascani.lang.util;

import java.util.Observable;

/**
 * This class is intended to be used in code generated by the Pascani DSL
 * 
 * @author Miguel Jim√©nez - Initial contribution and API
 */
public abstract class ManagedEvent extends Observable {
	
	protected volatile boolean paused = false;
	
	public void pause() {
		this.paused = true;
	}
	
	public void resume() {
		this.paused = false;
	}

}
